// BlogHeader.js

const BlogHeader = () => {
    return (
      <div className="relative h-[500px] bg-cover bg-center bg-no-repeat" 
           style={{ backgroundImage: `url('/path/to/your/image.jpg')` }}>
        <div className="absolute top-0 left-0 right-0 bottom-0 bg-black bg-opacity-50 flex justify-end items-center p-6 md:p-12">
          <div className="text-right text-white max-w-lg">
            <h1 className="text-3xl md:text-5xl font-bold leading-tight">عنوان اصلی بلاگ</h1>
            <p className="text-sm md:text-lg mt-4">این یک متن کوتاه است که باید در دو خط قرار بگیرد و توضیحاتی مختصر ارائه کند.</p>
          </div>
        </div>
      </div>
    );
  };
  
  export default BlogHeader;
