import React, { useState } from 'react';

const FilterBar = () => {
  const [activeTab, setActiveTab] = useState('همه');

  const tabs = ['همه', 'محبوب‌ترین‌ها', 'پربازدیدترین‌ها', 'جدیدترین‌ها'];

  return (
    <div className="flex justify-between items-center w-full mt-6 mb-4">
      {/* بخش تب‌ها */}
      <div className="flex bg-gray-100 rounded-full p-1 space-x-1 rtl:space-x-reverse">
        {tabs.map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`px-4 py-2 rounded-full focus:outline-none ${
              activeTab === tab
                ? 'bg-blue-500 text-white'
                : 'bg-white text-gray-600'
            }`}
          >
            {tab}
          </button>
        ))}
      </div>

      {/* فیلد جستجو */}
      <div className="relative w-[300px]">
        <input
          type="text"
          placeholder="دنبال چی میگردی"
          className="w-full py-2 pl-10 pr-4 text-gray-600 bg-gray-100 rounded-full focus:outline-none"
        />
        <span className="absolute left-3 top-2 text-gray-400">
          🔍
        </span>
      </div>
    </div>
  );
};

export default FilterBar;
