import React, { useState } from 'react';

const BlogsFilter = () => {
  const [selectedFilter, setSelectedFilter] = useState('همه');

  const filters = ['همه', 'محبوب‌ترین‌ها', 'پربازدیدترین‌ها', 'جدیدترین‌ها'];

  return (
    <div className="flex items-center justify-between bg-white p-4 rounded-lg mt-4 scale-95">
      <div className="flex space-x-4 bg-white shadow-md p-2 rounded-md"> {/* سایه به باکس دکمه‌ها */}
        {filters.map((filter) => (
          <button
            key={filter}
            onClick={() => setSelectedFilter(filter)}
            className={`px-4 py-2 rounded-full ${
              selectedFilter === filter
                ? 'bg-blue-500 text-white'
                : 'bg-white text-gray-700'
            } focus:outline-none`}
          >
            {filter}
          </button>
        ))}
      </div>

      {/* باکس سرچ */}
      <div className="flex items-center rounded-md overflow-hidden w-3/5 shadow-md"> {/* سایه به باکس سرچ */}
        <button className="px-4 text-gray-500 bg-transparent w-8 justify-center flex">
          🔍
        </button>
        <input
          type="text"
          placeholder="دنبال چی می‌گردی"
          className="w-full px-4 py-2 text-gray-700 focus:outline-none"
          style={{ borderRadius: '5px' }} // گوشه‌ها مستطیلی
        />
      </div>
    </div>
  );
};

export default BlogsFilter;
