import React from 'react';
import Header from './components/Header'; // ایمپورت هدر
import Footer from './components/Footer'; // ایمپورت فوتر

function Blogs() {
  // داده‌های فرضی برای پست‌ها
  const posts = [
    {
      id: 1,
      title: "First Blog Post",
      image: "https://via.placeholder.com/150",
      description: "This is a short description of the first post.",
    },
    {
      id: 2,
      title: "Second Blog Post",
      image: "https://via.placeholder.com/150",
      description: "This is a short description of the second post.",
    },
    {
      id: 3,
      title: "Third Blog Post",
      image: "https://via.placeholder.com/150",
      description: "This is a short description of the third post.",
    },
    // ادامه داده‌ها به همین شکل برای 9 پست
    // ...
    {
      id: 9,
      title: "Ninth Blog Post",
      image: "https://via.placeholder.com/150",
      description: "This is a short description of the ninth post.",
    },
  ];

  return (
    <div className="bg-gray-100 min-h-screen flex flex-col">
      <Header /> {/* نمایش هدر */}

      {/* بخش اصلی: نمایش پست‌ها */}
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-6 text-center">Blog Posts</h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {posts.map((post) => (
            <div key={post.id} className="bg-white rounded-lg shadow-md overflow-hidden">
              <img src={post.image} alt={post.title} className="w-full h-48 object-cover" />
              <div className="p-4">
                <h2 className="text-xl font-semibold mb-2">{post.title}</h2>
                <p className="text-gray-600">{post.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Pagination */}
      <div className="flex justify-center items-center py-6">
        <nav>
          <ul className="flex space-x-2">
            <li>
              <button className="px-3 py-1 rounded bg-gray-200 hover:bg-gray-300">1</button>
            </li>
            <li>
              <button className="px-3 py-1 rounded bg-gray-200 hover:bg-gray-300">2</button>
            </li>
            <li>
              <button className="px-3 py-1 rounded bg-gray-200 hover:bg-gray-300">3</button>
            </li>
            {/* اضافه کردن دکمه‌های بیشتر بر اساس تعداد صفحات */}
          </ul>
        </nav>
      </div>

      <Footer /> {/* نمایش فوتر */}
    </div>
  );
}

export default Blogs;
