import React, { useState } from 'react';

const FilterBar = () => {
  const [activeFilter, setActiveFilter] = useState('all');

  const handleFilterClick = (filter) => {
    setActiveFilter(filter);
  };

  return (
    <div className="flex items-center justify-between p-2 w-full">
      {/* دکمه‌های فیلتر */}
      <div className="flex items-center bg-white rounded-md p-1 space-x-2 rtl:space-x-reverse">
        <button
          className={`py-2 px-4 rounded-md ${activeFilter === 'all' ? 'bg-blue-500 text-white' : 'bg-white text-gray-700'}`}
          onClick={() => handleFilterClick('all')}
        >
          همه
        </button>
        <button
          className={`py-2 px-4 rounded-md ${activeFilter === 'popular' ? 'bg-blue-500 text-white' : 'bg-white text-gray-700'}`}
          onClick={() => handleFilterClick('popular')}
        >
          محبوب‌ترین‌ها
        </button>
        <button
          className={`py-2 px-4 rounded-md ${activeFilter === 'mostViewed' ? 'bg-blue-500 text-white' : 'bg-white text-gray-700'}`}
          onClick={() => handleFilterClick('mostViewed')}
        >
          پربازدیدترین‌ها
        </button>
        <button
          className={`py-2 px-4 rounded-md ${activeFilter === 'newest' ? 'bg-blue-500 text-white' : 'bg-white text-gray-700'}`}
          onClick={() => handleFilterClick('newest')}
        >
          جدیدترین‌ها
        </button>
      </div>

      {/* باکس جستجو */}
      <div className="flex items-center w-[900px] rounded-md pr-2 bg-transparent"> {/* عرض جستجو بیشتر شد و border برداشته شد */}
        <input
          type="text"
          placeholder="دنبال چی میگردی"
          className="w-full bg-transparent focus:outline-none text-gray-700 placeholder-gray-500 pl-2"
        />
        <button className="p-2">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-500" viewBox="0 0 20 20" fill="currentColor">
            <path fillRule="evenodd" d="M12.9 14.32a7.94 7.94 0 111.41-1.41l4.28 4.27a1 1 0 01-1.42 1.42l-4.27-4.28zm-1.47-6.9a6 6 0 100 12 6 6 0 000-12z" clipRule="evenodd" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default FilterBar;
