import React, { useState, useEffect } from 'react';
import './Login.css';

function Login() {
  const [mobileNumber, setMobileNumber] = useState('');
  const [isCodeSent, setIsCodeSent] = useState(false);
  const [timer, setTimer] = useState(120); // زمان تایمر به ثانیه (2 دقیقه)
  const [code, setCode] = useState(['', '', '', '', '']); // حالت برای کد تاییدیه
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isCodeConfirmed, setIsCodeConfirmed] = useState(false); // برای کنترل تایید کد

  useEffect(() => {
    let interval = null;
    if (isCodeSent && timer > 0) {
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
    } else if (timer === 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isCodeSent, timer]);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Mobile number:', mobileNumber);
    setIsCodeSent(true);
  };

  const handleCodeChange = (index, value) => {
    const newCode = [...code];
    newCode[index] = value;
    setCode(newCode);
  };

  const handleCodeSubmit = (e) => {
    e.preventDefault();
    // در اینجا می‌توانید منطق تأیید کد را اضافه کنید
    if (code.join('') === '12345') { // به جای 12345 منطق خود را قرار دهید
      setIsCodeConfirmed(true);
      setErrorMessage('');
    } else {
      setErrorMessage('کد تأیید نادرست است.');
    }
  };

  const handleRegistrationSubmit = (e) => {
    e.preventDefault();

    // اعتبارسنجی ایمیل
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailPattern.test(email)) {
      setErrorMessage('ایمیل معتبر نیست.');
      return;
    }

    // اعتبارسنجی رمز عبور
    if (password !== confirmPassword) {
      setErrorMessage('رمز عبور و تایید رمز عبور باید یکی باشند.');
      return;
    }

    // در اینجا می‌توانید اقدام به ثبت‌نام کاربر کنید
    console.log('Registration Info:', { email, username, password });
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs < 10 ? `0${secs}` : secs}`;
  };

  return (
    <div className="login-container">
      <button className="close-btn" onClick={() => setIsCodeSent(false)}>×</button>
      {isCodeSent ? (
        <>
          {isCodeConfirmed ? ( // اگر کد تأیید شده است
            <div>
              <h2>اطلاعات کاربری</h2>
              {errorMessage && <p className="error-message">{errorMessage}</p>}
              <form onSubmit={handleRegistrationSubmit}>
                <input
                  type="email"
                  placeholder="ایمیل"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <input
                  type="text"
                  placeholder="نام کاربری"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <input
                  type="password"
                  placeholder="رمز عبور"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <input
                  type="password"
                  placeholder="تایید رمز عبور"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                />
                <button type="submit">ثبت‌نام</button>
              </form>
            </div>
          ) : (
            <div>
              <h2>کد تاییدیه</h2>
              <p>
                کد به شماره {mobileNumber} ارسال شد. <a href="#">تغییر شماره</a>
              </p>
              <form onSubmit={handleCodeSubmit}>
                <div className="code-inputs">
                  {code.map((value, index) => (
                    <input
                      key={index}
                      type="text"
                      maxLength="1"
                      value={value}
                      onChange={(e) => handleCodeChange(index, e.target.value)}
                      required
                    />
                  ))}
                </div>
                <p>{formatTime(timer)}</p>
                <button type="submit">تایید کد</button>
              </form>
              {errorMessage && <p className="error-message">{errorMessage}</p>}
              <p>
                کد ارسال نشد؟ <a href="#">ارسال دوباره</a>
              </p>
            </div>
          )}
        </>
      ) : (
        <div>
          <h2>ساخت حساب کاربری</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="شماره موبایل"
              value={mobileNumber}
              onChange={(e) => setMobileNumber(e.target.value)}
              required
            />
            <button type="submit">دریافت کد تایید</button>
          </form>
          <p>
            حساب کاربری دارید؟ <a href="#">وارد شوید</a>
          </p>
        </div>
      )}
    </div>
  );
}

export default Login;
