import React from 'react';

function HeroSection() {
  return (
    <div className="relative bg-white">
      {/* بخش با تصویر بک‌گراند */}
      <div
        className="bg-cover bg-center h-96 relative flex items-center justify-center"
        style={{ backgroundImage: 'url("/path-to-your-image.png")' }} // تصویر بک‌گراند
      >
        {/* لایه‌ی نیمه شفاف برای ایجاد افکت روی تصویر */}
        <div className="absolute inset-0 bg-white opacity-80"></div>

        {/* متن‌ها */}
        <div className="relative z-10 text-center">
          <h1 className="text-4xl font-bold text-gray-800">
            اخبار و مقالات هگزاسکواد
          </h1>
          <p className="mt-4 text-lg text-gray-600">
            به‌روزترین مقالات در زمینه برنامه‌نویسی و تکنولوژی را در اینجا دنبال کنید.
          </p>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
