import React from 'react';

const Blogs = () => {
  return (
    <div className="relative w-full h-screen bg-blue-50">
      {/* Container اصلی که کل محتوا رو می‌گیره */}
      <div 
        className="absolute top-0 left-0 w-full h-full bg-no-repeat bg-cover bg-center"
        style={{ backgroundImage: 'url("/image.png")' }}
      >
        {/* اینجا تصویر بکگراند تنظیم شده */}
      </div>

      {/* متن روی تصویر */}
      <div className="relative z-10 container mx-auto px-6 py-12 flex items-center justify-between h-full">
        
        {/* متن سمت راست */}
        <div className="w-full md:w-1/2 text-right">
          <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4">
            اخبار و مقالات هگزاسکواد
          </h1>
          <p className="text-gray-600 text-lg leading-relaxed">
            آموزش برنامه‌نویسی یکی از دوره‌های محبوب در حوزه فناوری اطلاعات است.
            برنامه‌نویسی مهارتی است که به افراد امکان می‌دهد تا با نرم‌افزارهای 
            کامپیوتری ارتباط برقرار کنند و آنها را توسعه دهند.
          </p>
        </div>

        {/* فضای خالی برای تصویر (سمت چپ) */}
        <div className="hidden md:block w-full md:w-1/2">
          {/* این بخش خالی است، چون متن روی عکس باید باشه */}
        </div>
      </div>
    </div>
  );
};

export default Blogs;
