import React, { useState } from 'react';
import axios from 'axios';
import './Login.css'; // اضافه کردن فایل CSS

const Login = () => {
  // State ها برای مراحل مختلف
  const [step, setStep] = useState(1); // برای تعیین مرحله
  const [mobileNumber, setMobileNumber] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  // تابع ارسال شماره تلفن
  const sendPhoneNumber = async () => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/Sign/SendVerifyMessage`, {
        phoneNumber: mobileNumber
      });
      console.log('پیام تایید ارسال شد:', response.data);
      setStep(2); // بعد از ارسال موفقیت‌آمیز به مرحله تایید کد بروید
    } catch (error) {
      console.error('خطا در ارسال شماره تلفن:', error);
    }
  };

  // تابع تایید کد
  const verifyCode = async () => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/Sign/VerifyMessage`, {
        code: verificationCode
      });
      console.log('کد تایید شد:', response.data);
      setStep(3); // بعد از تایید کد به مرحله ثبت‌نام بروید
    } catch (error) {
      console.error('خطا در تایید کد:', error);
    }
  };

  // تابع ثبت‌نام
  const registerUser = async () => {
    if (password !== confirmPassword) {
      alert('رمز عبور و تایید رمز عبور یکسان نیستند');
      return;
    }

    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/Sign/Register`, {
        gmail: email,
        password: password,
        phoneNumber: mobileNumber
      });
      console.log('کاربر با موفقیت ثبت شد:', response.data);
    } catch (error) {
      console.error('خطا در ثبت نام کاربر:', error);
    }
  };

  return (
    <div className="login-form">
      {step === 1 && (
        <div>
          <h2>ورود</h2>
          <input
            type="text"
            value={mobileNumber}
            onChange={(e) => setMobileNumber(e.target.value)}
            placeholder="شماره موبایل خود را وارد کنید"
          />
          <button onClick={sendPhoneNumber}>ارسال کد تایید</button>
        </div>
      )}

      {step === 2 && (
        <div>
          <h2>تایید کد</h2>
          <input
            type="text"
            value={verificationCode}
            onChange={(e) => setVerificationCode(e.target.value)}
            placeholder="کد تایید را وارد کنید"
          />
          <button onClick={verifyCode}>تایید کد</button>
        </div>
      )}

      {step === 3 && (
        <div>
          <h2>ثبت‌نام</h2>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="ایمیل"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="رمز عبور"
          />
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="تایید رمز عبور"
          />
          <button onClick={registerUser}>ثبت‌نام</button>
        </div>
      )}
    </div>
  );
};

export default Login;
