// BlogHeader.js

const BlogHeader = () => {
  return (
    <div className="relative w-full h-[60vh] bg-cover bg-center bg-no-repeat" 
         style={{ backgroundImage: `url('/path/to/your/image.jpg')` }}>
      <div className="absolute top-0 left-0 right-0 bottom-0 bg-black bg-opacity-40 flex justify-end items-center p-4 lg:p-8">
        <div className="text-right text-white max-w-lg">
          <h1 className="text-4xl lg:text-5xl font-bold leading-tight">عنوان اصلی بلاگ</h1>
          <p className="text-base lg:text-lg mt-2 lg:mt-4">این متن باید در دو خط باشد و توضیحات مختصر بلاگ را نشان دهد.</p>
        </div>
      </div>
    </div>
  );
};

export default BlogHeader;
