import React from 'react';

const FilterBar = () => {
  return (
    <div className="flex items-center justify-between p-4 bg-white rounded-md w-full">
      {/* فیلترها */}
      <div className="flex items-center space-x-4 rtl:space-x-reverse">
        <button className="bg-blue-500 text-white py-2 px-4 rounded-md">همه</button>
        <button className="text-gray-700 py-2 px-4 rounded-md hover:bg-gray-200">محبوب‌ترین‌ها</button>
        <button className="text-gray-700 py-2 px-4 rounded-md hover:bg-gray-200">پربازدیدترین‌ها</button>
        <button className="text-gray-700 py-2 px-4 rounded-md hover:bg-gray-200">جدیدترین‌ها</button>
      </div>
      
      {/* جستجو */}
      <div className="flex items-center bg-gray-100 rounded-md px-4 py-2 w-64">
        <input 
          type="text" 
          placeholder="دنبال چی میگردی" 
          className="bg-transparent w-full focus:outline-none text-gray-700 placeholder-gray-500"
        />
        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-500" viewBox="0 0 20 20" fill="currentColor">
          <path fillRule="evenodd" d="M12.9 14.32a7.94 7.94 0 111.41-1.41l4.28 4.27a1 1 0 01-1.42 1.42l-4.27-4.28zm-1.47-6.9a6 6 0 100 12 6 6 0 000-12z" clipRule="evenodd" />
        </svg>
      </div>
    </div>
  );
};

export default FilterBar;
